import React from 'react';


class Tagbutton extends React.Component{




    constructor(){
        super()
        this.state ={
            initial:1,
            buttonW:0,
            clicked:0,
            buttonContent:{
                transition:'0.15s ease-out all',
                width:'auto',
                height:'45px',
                borderStyle: 'solid',
                borderWidth: '0px',
                borderColor:'#000',
                borderRadius: '6px',
                color:'#000',
                overflow: 'hidden',
                textAlign: 'center',
                display:'flex',
                cursor: 'pointer',
                justifyContent: 'center',
                marginRight:6,
                marginTop:6,
                float:'left',
            },
            buttoninsidebg:{
                transition:'0.15s ease-out all',
                width:'100%',
                height:'45px',
                float:'left',
                backgroundColor:'#999',
            },
        }

        this.changevalue =this.changevalue.bind(this)

    }

    changevalue(){

        this.setState(prevState=>{
            return{
                initial:prevState.initial +1
            }
        })
    }

    componentWillMount() {
        let bgc='#999'
        if(this.props.checked==1){
            bgc='#999'
        }else{
            bgc=this.props.bgcolor
        }

        if(this.props.ButtonName){
            const btnW=this.props.ButtonName.length*20
            this.setState({
                buttonContent:{
                    transition:'0.15s ease-out all',
                    width:btnW,
                    height:'45px',
                    borderStyle: 'solid',
                    borderWidth: '0px',
                    borderColor:'#000',
                    borderRadius: '6px',
                    color:'#000',
                    overflow: 'hidden',
                    textAlign: 'center',
                    display:'flex',
                    cursor: 'pointer',
                    justifyContent: 'center',
                    marginRight:6,
                    marginTop:6,
                    float:'left',
                },
                buttoninsidebg:{
                    transition:'0.15s ease-out all',
                    width:'100%',
                    height:'45px',
                    float:'left',
                    backgroundColor:bgc,
                },
            })
        }

    }

    componentWillReceiveProps(){

        this.state.clicked=this.props.checked

        let bgc='#999'
        if(this.props.checked==1){
            bgc='#999'
        }else{
            bgc=this.props.bgcolor
        }
        this.setState({
                buttoninsidebg:{
                    transition:'0.15s ease-out all',
                    width:'100%',
                    height:'45px',
                    float:'left',
                    backgroundColor:bgc,
                },
            }
        )


    }


    mouseEnter(event) {

        let bgc='#999'
        if(this.props.checked==1){
            bgc='#999'
        }else{
            bgc=this.props.bgcolor
        }

        this.setState({
            buttoninsidebg:{
                transition:'0.15s ease-out all',
                width:'100%',
                height:'45px',
                float:'left',
                backgroundColor:bgc,
            },
            }
        )

    }

    mouseOut(event) {
        let bgc='#999'
        if(this.props.checked==1){
            bgc='#999'
        }else{
            bgc=this.props.bgcolor
        }
        this.setState({
            buttoninsidebg:{
                transition:'0.15s ease-out all',
                width:'100%',
                height:'45px',
                float:'left',
                backgroundColor:bgc,
            },
            }
        )
    }

    mouseDown(event){


        let bgc='#999'
        if( this.state.clicked==0){
          this.setState({clicked:1})
            bgc='#999'
        }else{
            this.setState({clicked:0})
            bgc=this.props.bgcolor
        }

        this.setState({
                buttoninsidebg:{
                    transition:'0.15s ease-out all',
                    width:'100%',
                    height:'45px',
                    float:'left',
                    backgroundColor:bgc,
                },

            }
        )

        this.props.action(this.props.ButtonName)

    }

    render(){

        const styles={

            buttonInfo:{

                alignItems: 'center',
                display: 'flex',
                fontSize:'14px',
                justifyContent: 'center',
                verticalAlign:'center',
                textAlign:'center',
                width:'auto',
                color:'#000',
                fontWeight:'bold',
                height:'45px',
                position:'absolute',


            }

        }

        return(
            <div style={this.state.buttonContent} onClick={()=>{this.mouseDown() }}  onMouseEnter={()=>{this.mouseEnter() }}  onMouseLeave={()=>{this.mouseOut() }}>
                <div style={styles.buttonInfo} >
                    {this.props.ButtonName}
                </div>
                <div  style={this.state.buttoninsidebg} >

                </div>

            </div>
        )

    }
}



export default Tagbutton
